using System;
using System.IO;
using System.Threading.Tasks;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Azure.WebJobs;
using Microsoft.Azure.WebJobs.Extensions.Http;
using Microsoft.AspNetCore.Http;
using Microsoft.Extensions.Logging;
using Newtonsoft.Json;
using System.Text.Json;
using Alexa.NET;
using Alexa.NET.Request;
using Alexa.NET.Request.Type;
using Alexa.NET.Response;
using System.Collections.Generic;

namespace Yokai.Azure
{
    public static class Whisper
    {
        public static InMemoryYokaiStore  YokaiStore {get; private set;} = new InMemoryYokaiStore(System.Text.Json.JsonSerializer.Deserialize<IEnumerable<Yokai>>(GetYokaisJson()));

        [FunctionName("Whisper")]
        public static async Task<SkillResponse> Run(
            [HttpTrigger(AuthorizationLevel.Function, "get", "post", Route = null)] HttpRequest req,
            ILogger log)
        {
            try
            {
                log.LogInformation("C# Whisper HTTP trigger function received a request.");

                string json = await req.ReadAsStringAsync();
                var skillRequest = JsonConvert.DeserializeObject<SkillRequest>(json);

                if (skillRequest.Request is LaunchRequest)
                {
                    var response=ResponseBuilder.Ask("Hola mi corpóreo amigo, soy Whísper. A tu servicio.",null);
                    var card = new StandardCard()
                    {
                        Title = "Whisper:",
                        Content = "Hola mi corpóreo amigo, soy Whisper. A tu servicio."
                    };
                    card.Image = new CardImage
                    {
                        SmallImageUrl = GetYokaiImageUrl("Whisper.png")
                    };
                    card.Image.LargeImageUrl = card.Image.SmallImageUrl;

                    response.Response.Card = card;

                    return response;
                }

                var intentRequest = skillRequest.Request as IntentRequest;

                if (intentRequest.Intent.Name.Equals("RandomYokai"))
                {
                    var randomYokai = YokaiStore.RandomYokai();
                    if (randomYokai != null)
                    {
                        var response = ResponseBuilder.Ask(GetYokaiDescription(randomYokai), null);
                        return BuildYokaiResponse(randomYokai, response);
                    }
                    return ResponseBuilder.Ask("Oooops. Parece que no soy digno de llamarme Whísper, no he encontrado ningún Yokai.", null);
                }
            }
            catch(Exception e)
            {
                log.LogError(e, "Error processing request");
            }
            return new SkillResponse();
        }

        private static SkillResponse BuildYokaiResponse(Yokai randomYokai, SkillResponse response)
        {
            var imageUrl = GetYokaiImageUrl(randomYokai.ImageFileName);
            if (string.IsNullOrEmpty(imageUrl))
                return response;

            var card = new StandardCard()
            {
                Title = randomYokai.Name,
                Content = randomYokai.Biography
            };
            card.Image = new CardImage
            {
                SmallImageUrl = imageUrl,
                LargeImageUrl=imageUrl
            };


            response.Response.Card = card;

            return response;
        }

        private static string GetYokaiDescription(Yokai yokai)
        {
            return $"Yokai {yokai.Name}, de la tribu {yokai.Tribe}. {yokai.Biography}";
        }

        private static string GetYokaiImageUrl(string imageFileName)
        {
            var yokaiBaseUrl = Environment.GetEnvironmentVariable("YOKAIS_BASE_URL") ?? string.Empty;
            if(!yokaiBaseUrl.EndsWith("/"))
                yokaiBaseUrl = yokaiBaseUrl + "/";
            
            
            return yokaiBaseUrl + System.Web.HttpUtility.UrlEncode(imageFileName);

        }

